import{_ as a,a as e,b as t,c as d}from"./controllability.513f4d45.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";import{l as s,k as l,y as f}from"./framework.506ba982.js";const k='{"title":"Design","description":"","frontmatter":{"title":"Design","lang":"en-US"},"headers":[{"level":2,"title":"Consistency","slug":"consistency"},{"level":2,"title":"Feedback","slug":"feedback"},{"level":2,"title":"Efficiency","slug":"efficiency"},{"level":2,"title":"Controllability","slug":"controllability"}],"relativePath":"en-US/guide/design.md","lastUpdated":1641920373679}',n={},r=f('<h1 id="design-disciplines" tabindex="-1" data-v-d7f83f84>Design Disciplines <a class="header-anchor" href="#design-disciplines" aria-hidden="true" data-v-d7f83f84>#</a></h1><div class="guide__design" data-v-d7f83f84><div class="el-row cards" style="margin-left:-7px;margin-right:-7px;" data-v-d7f83f84><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-d7f83f84><div class="card" data-v-d7f83f84><img src="'+a+'" alt="Consistency" data-v-d7f83f84><p data-v-d7f83f84>Consistency</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-d7f83f84><div class="card" data-v-d7f83f84><img src="'+e+'" alt="Feedback" data-v-d7f83f84><p data-v-d7f83f84>Feedback</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-d7f83f84><div class="card" data-v-d7f83f84><img src="'+t+'" alt="Efficiency" data-v-d7f83f84><p data-v-d7f83f84>Efficiency</p></div></div><div class="el-col el-col-24 el-col-xs-12 el-col-sm-6 is-guttered" data-v-d7f83f84><div class="card" data-v-d7f83f84><img src="'+d+'" alt="Controllability" data-v-d7f83f84><p data-v-d7f83f84>Controllability</p></div></div></div></div><h2 id="consistency" tabindex="-1" data-v-d7f83f84>Consistency <a class="header-anchor" href="#consistency" aria-hidden="true" data-v-d7f83f84>#</a></h2><ul data-v-d7f83f84><li data-v-d7f83f84><p data-v-d7f83f84><strong data-v-d7f83f84>Consistent with real life:</strong> in line with the process and logic of real life, and comply with languages and habits that the users are used to.</p></li><li data-v-d7f83f84><p data-v-d7f83f84><strong data-v-d7f83f84>Consistent within interface:</strong> all elements should be consistent, such as: design style, icons and texts, position of elements, etc.</p></li></ul><h2 id="feedback" tabindex="-1" data-v-d7f83f84>Feedback <a class="header-anchor" href="#feedback" aria-hidden="true" data-v-d7f83f84>#</a></h2><ul data-v-d7f83f84><li data-v-d7f83f84><p data-v-d7f83f84><strong data-v-d7f83f84>Operation feedback:</strong> enable the users to clearly perceive their operations by style updates and interactive effects.</p></li><li data-v-d7f83f84><p data-v-d7f83f84><strong data-v-d7f83f84>Visual feedback:</strong> reflect current state by updating or rearranging elements of the page.</p></li></ul><h2 id="efficiency" tabindex="-1" data-v-d7f83f84>Efficiency <a class="header-anchor" href="#efficiency" aria-hidden="true" data-v-d7f83f84>#</a></h2><ul data-v-d7f83f84><li data-v-d7f83f84><p data-v-d7f83f84><strong data-v-d7f83f84>Simplify the process:</strong> keep operating process simple and intuitive.</p></li><li data-v-d7f83f84><p data-v-d7f83f84><strong data-v-d7f83f84>Definite and clear:</strong> enunciate your intentions clearly so that the users can quickly understand and make decisions.</p></li><li data-v-d7f83f84><p data-v-d7f83f84><strong data-v-d7f83f84>Easy to identify:</strong> the interface should be straightforward, which helps the users to identify and frees them from memorizing and recalling.</p></li></ul><h2 id="controllability" tabindex="-1" data-v-d7f83f84>Controllability <a class="header-anchor" href="#controllability" aria-hidden="true" data-v-d7f83f84>#</a></h2><ul data-v-d7f83f84><li data-v-d7f83f84><p data-v-d7f83f84><strong data-v-d7f83f84>Decision making:</strong> giving advice about operations is acceptable, but do not make decisions for the users.</p></li><li data-v-d7f83f84><p data-v-d7f83f84><strong data-v-d7f83f84>Controlled consequences:</strong> users should be granted the freedom to operate, including canceling, aborting or terminating current operation.</p></li></ul>',10),o=[r];function c(v,p,g,h,u,_){return l(),s("div",null,o)}var x=i(n,[["render",c],["__scopeId","data-v-d7f83f84"]]);export{k as __pageData,x as default};
