import{_ as i}from"./chunks/design-guide.4bba2082.js";import{o as n,c as o,a as s,b as e,d as t,e as r}from"./app.2eb9cc6e.js";const l=e("h1",{id:"directrices-de-diseno",tabindex:"-1"},[t("Directrices de Dise\xF1o "),e("a",{class:"header-anchor vp-link",href:"#directrices-de-diseno","aria-hidden":"true"},"#")],-1),c=r('<h2 id="consistencia" tabindex="-1">Consistencia <a class="header-anchor vp-link" href="#consistencia" aria-hidden="true">#</a></h2><ul><li><p><strong>Consistencia con la vida real:</strong> En l\xEDnea con la l\xF3gica de la vida real y cumpliendo con el lenguaje y los h\xE1bitos a los que est\xE1n acostumbrados los usuarios.</p></li><li><p><strong>Consistencia desde el interior de la interfaz:</strong> Todos los elementos deben ser consistentes en estilo de dise\xF1o, iconos y textos, posici\xF3n de elementos, etc.</p></li></ul><h2 id="retroalimentacion-feedback" tabindex="-1">Retroalimentaci\xF3n (Feedback) <a class="header-anchor vp-link" href="#retroalimentacion-feedback" aria-hidden="true">#</a></h2><ul><li><p><strong>Feedback Operativo:</strong> Permite a los usuarios identificar claramente sus acciones gracias a las actualizaciones de estilo y efectos interactivos.</p></li><li><p><strong>Feedback visual:</strong> Refleja el estado actual delcontenido actualizando o reorganizando elementos de la p\xE1gina.</p></li></ul><h2 id="eficiencia" tabindex="-1">Eficiencia <a class="header-anchor vp-link" href="#eficiencia" aria-hidden="true">#</a></h2><ul><li><p><strong>Simplificar el proceso:</strong> Mantener el proceso operativo simple e intuitivo.</p></li><li><p><strong>Claro y Preciso:</strong> Exponer un ainterfaz clara para que los usuarios puedan entender y tomar decisiones r\xE1pidamente.</p></li><li><p><strong>F\xE1cil de entender:</strong> La interfaz debe ser directa, sin excesivos pasos intermedios, lo que ayuda a los usuarios a identificar y los libera de memorizar y retirar.</p></li></ul><h2 id="controlabilidad" tabindex="-1">Controlabilidad <a class="header-anchor vp-link" href="#controlabilidad" aria-hidden="true">#</a></h2><ul><li><p><strong>La toma de decisiones:</strong> Dar consejos sobre las opciones disponibles es aceptable, pero no tomar decisiones por los usuarios.</p></li><li><p><strong>Consecuencias controladas:</strong> Los usuarios deben tener la libertad de operar pudiendo errar, incluyendo cancelar, abortar o terminar la operaci\xF3n actual.</p></li></ul>',8),m='{"title":"Design","description":"","frontmatter":{"title":"Design","lang":"es-ES"},"headers":[{"level":2,"title":"Consistencia","slug":"consistencia"},{"level":2,"title":"Retroalimentaci\xF3n (Feedback)","slug":"retroalimentacion-feedback"},{"level":2,"title":"Eficiencia","slug":"eficiencia"},{"level":2,"title":"Controlabilidad","slug":"controlabilidad"}],"relativePath":"es-ES/guide/design.md","lastUpdated":null}',d={},b=Object.assign(d,{__name:"design",setup(u){return(p,g)=>{const a=i;return n(),o("div",null,[l,s(a),c])}}});export{m as __pageData,b as default};
